h4 { color: #606060; font-size: 0.8rem; height: 1.4rem; line-height: 1.4rem; margin: 0; }

h6 { font-size: 0.9rem; font-weight: normal; text-align: left; margin: 0; line-height: 1.3rem; }

legend, h5 { color: #606060; font-size: 0.7rem; text-transform: uppercase; }

.section--primary { margin-bottom: 4pt; }

.section--primary .section__inner { padding: 0.01pt 3pt 3pt 3pt; border: 2pt solid #909090; border-top: 0 none; padding: 1pt 2pt; margin-bottom: 3pt; background: rgba(255, 255, 255, 0.5); }

.section--primary .section__inner hr { margin-left: -4pt; margin-right: -4pt; }

