/* Classes */
.class-icon.icon_cleric { background-image: url('data:image/png;base64,'); }

.class-icon.icon_box { width: 6rem; height: 6rem; margin-left: auto; margin-right: auto; flex: 0 0 auto; border: 1px solid #b0b0b0; }

.field input { color: #505050; background: none; }

.field.field--temp .field__box { border-bottom: 2pt solid #b0b0b0; }

.field.field--temp .field__frame { margin-bottom: -1pt; }

.field.field--temp .field__box.field__box--border_full { border: 2pt solid #b0b0b0; border-radius: 4pt; outline-radius: 4pt; -moz-outline-radius: 4pt; }

.field.field--temp.field--icon .field__box.field__box--border_full { border-left: 0 none; }

.field__box { border-bottom: 0.4pt solid #787878; }

.field--icon .field__box, .field__box.field__box--border_full { border-radius: 2pt; outline-radius: 2pt; -moz-outline-radius: 2pt; }

.field__box.field__box--border_full { border: 0.75pt solid #787878; background: white; }

.field--icon .field__box.field__box--border_full { border-left-width: 0; }

.field--ref .field__box { border-bottom: 0.75pt dashed #787878; }

.field__box.field__box--border_none { border: 0 none; }

article header .field__box { border-bottom-width: 0.75pt; }

.field__control u { font-size: 1rem; font-family: "Merriweather", Georgia, 'Times New Roman', Times, serif; font-weight: 900; color: #787878; opacity: 0.2; }

.field__control--control_checkbox, .field--control_checkgrid .field__control { align-items: center; justify-content: center; }

.field__control--control_checkbox input[type=checkbox], .field--control_checkgrid .field__control input[type=checkbox] { display: none; }

.field__control--control_checkbox input[type=checkbox] + label, .field--control_checkgrid .field__control input[type=checkbox] + label { background: white; border: 0.5pt solid #787878; width: 4pt; height: 4pt; }

.field__control--control_checkbox input[type=checkbox]:checked + label, .field--control_checkgrid .field__control input[type=checkbox]:checked + label { background: #787878; }

.field__control--control_radio { align-items: center; justify-content: center; }

.field__control--control_radio input[type=radio] { display: none; }

.field__control--control_radio input[type=radio] + label { background: white; border: 0.5pt solid #787878; width: 5pt; height: 5pt; border-radius: 50%; }

.field__control--control_radio input[type=radio]:checked + label { background: #787878; }

.field__box label.field__separator { margin-left: 2pt; }

.field__box label.field__separator:after { content: "\0203A"; }

.field.field--control_alignment { width: 4.5rem; }

.field.field--control_alignment .field__frame { display: block; position: relative; width: 4.5rem; height: 4.5rem; }

